<?php$servername = "localhost";$username = "root";$password = "";$dbname = "chart";// Membuat koneksi$conn = new mysqli($servername, $username, $password, $dbname);// Memeriksa koneksiif ($conn->connect_error) {    die("Koneksi gagal: " . $conn->connect_error);}// Mengambil data dari tabel chart_data$sql = "SELECT Humid,Time FROM dat2 LIMIT 12";$result = $conn->query($sql);$data= array();if ($result->num_rows > 0) {    while($row = $result->fetch_assoc()) {        $data[] = $row;    }}$conn->close();// Menutup koneksi// Mengembalikan data sebagai JSONheader('Content-Type: application/json');echo json_encode($data);?>