<?php$hostname = "localhost";$username = "root";$password = "";$database = "chart";$conn = mysqli_connect($hostname, $username, $password, $database);if (!$conn) {    die("Connection failed: " . mysqli_connect_error());}echo "Database OK";if (isset($_POST["Suhu"]) && isset($_POST["Kelemebapan"])) {    $tem = $_POST["Suhu"];    $hum = $_POST["Kelemebapan"];          $sql = "INSERT INTO 'data' (Suhu, Kelemebapan) VALUES (" . $tem . "," . $hum . ")";    if (mysqli_query($conn, $sql)) {        echo "Data terkirim";    } else {        echo "Error: " . $sql . "<br>" . mysqli_error($conn);    }}?>