<!DOCTYPE html><html><head>    <title>Contoh Chart.js</title>    <script src="https://cdn.jsdelivr.net/npm/chart.js"></script></head><body>    <?php        // Data untuk chart 1        $chart1_labels = json_encode(['Jan', 'Feb', 'Mar', 'Apr', 'May']);        $chart1_data = json_encode([10, 20, 30, 40, 50]);        // Data untuk chart 2        $chart2_labels = json_encode(['Red', 'Blue', 'Yellow', 'Green', 'Purple']);        $chart2_data = json_encode([15, 25, 35, 45, 55]);        // Data untuk chart 3        $chart3_labels = json_encode(['A', 'B', 'C', 'D', 'E']);        $chart3_data = json_encode([12, 22, 32, 42, 52]);        // Data untuk chart 4        $chart4_labels = json_encode(['One', 'Two', 'Three', 'Four', 'Five']);        $chart4_data = json_encode([18, 28, 38, 48, 58]);    ?>    <div style="width: 50%; float: left;">        <canvas id="chart1"></canvas>    </div>    <div style="width: 50%; float: left;">        <canvas id="chart2"></canvas>    </div>    <div style="width: 50%; float: left;">        <canvas id="chart3"></canvas>    </div>    <div style="width: 50%; float: left;">        <canvas id="chart4"></canvas>    </div>    <script>        // Inisialisasi chart 1        var ctx1 = document.getElementById('chart1').getContext('2d');        var chart1 = new Chart(ctx1, {            type: 'line',            data: {                labels: <?php echo $chart1_labels; ?>,                datasets: [{                    label: 'Chart 1',                    data: <?php echo $chart1_data; ?>,                    backgroundColor: 'rgba(255, 99, 132, 0.2)',                    borderColor: 'rgba(255, 99, 132, 1)',                    borderWidth: 1                }]            },            options: {                responsive: true,                scales: {                    y: {                        beginAtZero: true                    }                }            }        });        // Inisialisasi chart 2        var ctx2 = document.getElementById('chart2').getContext('2d');        var chart2 = new Chart(ctx2, {            type: 'line',            data: {                labels: <?php echo $chart2_labels; ?>,                datasets: [{                    label: 'Chart 2',                    data: <?php echo $chart2_data; ?>,                    backgroundColor: 'rgba(54, 162, 235, 0.2)',                    borderColor: 'rgba(54, 162, 235, 1)',                    borderWidth: 1                }]            },            options: {                responsive: true,                scales: {                    y: {                        beginAtZero: true                    }                }            }        });        // Inisialisasi chart 3        var ctx3 = document.getElementById('chart3').getContext('2d');        var chart3 = new Chart(ctx3, {            type: 'line',            data: {                labels: <?php echo $chart3_labels; ?>,                datasets: [{                    label: 'Chart 3',                    data: <?php echo $chart3_data; ?>,                    backgroundColor: 'rgba(75, 192, 192, 0.2)',                    borderColor: 'rgba(75, 192, 192, 1)',                    borderWidth: 1                }]            },            options: {                responsive: true,                scales: {                    y: {                        beginAtZero: true                    }                }            }        });        // Inisialisasi chart 4        var ctx4 = document.getElementById('chart4').getContext('2d');        var chart4 = new Chart(ctx4, {            type: 'line',            data: {                labels: <?php echo $chart4_labels; ?>,                datasets: [{                    label: 'Chart 4',                    data: <?php echo $chart4_data; ?>,                    backgroundColor: 'rgba(255, 206, 86, 0.2)',                    borderColor: 'rgba(255, 206, 86, 1)',                    borderWidth: 1                }]            },            options: {                responsive: true,                scales: {                    y: {                        beginAtZero: true                    }                }            }        });    </script></body></html>