<?php// Database connection$connection = new PDO("mysql:host=localhost;dbname=chart", "root", "");// Number of records per page$limit = 100;// Current page$page = isset($_GET['page']) ? $_GET['page'] : 1;$start = ($page - 1) * $limit;// Fetch total records$totalRecordsQuery = $connection->query("SELECT COUNT(*) as total FROM dat");$totalRecords = $totalRecordsQuery->fetch()['total'];// Calculate total pages$totalPages = ceil($totalRecords / $limit);// Fetch records for current page$query = $connection->query("SELECT * FROM dat LIMIT $start, $limit");$records = $query->fetchAll();echo '<table>  <thead>    <tr>      <th>ID</th>      <th>Name</th>      <th>Email</th>    </tr>  </thead>  <tbody>';// Display recordsforeach ($records as $record) {	 echo '<tr>';    echo '<td>' . $record['Time'] . '</td>';    echo '<td>' . $record['Suhu'] . '</td>';    echo '<td>' . $record['Humid'] . '</td>';    echo '</tr>';      echo '</tbody></table>';    // Display record details   }// Display pagination linksecho '<ul class="pagination">';// Previous page linkif ($page > 1) {    echo '<li class="page-item"><a class="page-link" href="?page='.($page - 1).'">&laquo;</a></li>';}// Page linksfor ($i = 1; $i <= $totalPages; $i++) {    echo '<li class="page-item '.($page == $i ? 'active' : '').'"><a class="page-link" href="?page='.$i.'">'.$i.'</a></li>';}// Next page linkif ($page < $totalPages) {    echo '<li class="page-item"><a class="page-link" href="?page='.($page + 1).'">&raquo;</a></li>';}echo '</ul>';?> 